const navMobileMenu = document.querySelector(".nav_mobile-team");
navMobileMenu.addEventListener("click", () => {
    navMobileMenu.classList.toggle("nav_mobile-team_active");
    const e = navMobileMenu.nextElementSibling;
    e.style.maxHeight ? e.style.maxHeight = null : e.style.maxHeight = e.scrollHeight + "px"
});
const burger = document.querySelector(".burger"),
    navMenu = document.querySelector(".nav_mobile"),
    nav = document.querySelector(".nav");
burger.addEventListener("click", () => {
    burger.classList.toggle("burger_active"), nav.classList.toggle("nav_fixed"), navMenu.classList.toggle("nav_mobile-active")
}), setInterval(() => {
    window.outerWidth >= 768 && (burger.classList.remove("burger_active"), nav.classList.remove("nav_fixed"), navMenu.classList.remove("nav_mobile-active"))
}, 500);
const swiper = new Swiper("#js-carousel", {
    slidesPerView: 7,
    speed: 2e3,
    loop: !0,
    autoplay: {
        delay: 0,
        disableOnInteraction: !0
    },
    breakpoints: {
        1024: {
            slidesPerView: 9
        },
        991: {
            slidesPerView: 5
        },
        767: {
            slidesPerView: 4
        },
        575: {
            slidesPerView: 3
        }
    }
});